import './css/Section5.css';
import phone5sec from './media/iPhone14Pro.png';
import triangle_play from './media/Polygon.svg';
import { motion, useTransform, useScroll } from "framer-motion";
import { useRef } from "react";


function Section5(){
    return(
    <section className="world-inside">
        <HorizontalScrollCarousel />
    </section>
    );
}
const HorizontalScrollCarousel = () =>{
    const targetRef = useRef(null);
    const {scrollYProgress} = useScroll({
        target: targetRef,
    });
    const x = useTransform(scrollYProgress, [0, 1], ["0%", "-95%"]);
    return (
        <section ref={targetRef} className="relative h-[300vh] bg-neutral-900">
          <div className="sticky top-0 flex h-screen items-center overflow-hidden justify-start">
            <motion.div style={{ x }} className="flex gap-4">
              {cards.map((card) => {
                return <Card card={card} key={card.id} />;
              })}
            </motion.div>
          </div>
        </section>
      );
};
const Card = ({ card }) => {
    return (
      <div
        key={card.id}
        className="group relative h-[450px] w-[450px] overflow-hidden bg-neutral-200">
      </div>
    );
  };

export default Section5;

const cards = [
    {
      url: "./media/anna-pavlin-UMH92TvHEz0-unsplash.jpg",
      title: "Title 1",
      id: 1,
    },
    {
      url: "/imgs/abstract/2.jpg",
      title: "Title 2",
      id: 2,
    },
    {
      url: "/imgs/abstract/3.jpg",
      title: "Title 3",
      id: 3,
    },
    {
      url: "/imgs/abstract/4.jpg",
      title: "Title 4",
      id: 4,
    },
    {
      url: "/imgs/abstract/5.jpg",
      title: "Title 5",
      id: 5,
    },
    {
      url: "/imgs/abstract/6.jpg",
      title: "Title 6",
      id: 6,
    },
    {
      url: "/imgs/abstract/7.jpg",
      title: "Title 7",
      id: 7,
    },
  ];
